```js
export const arraysEqual = (arr1, arr2) => {
	// 首先，比较两个数组的长度
	if (arr1.length !== arr2.length) {
		return false;
	}

	// 如果两个数组长度相同，再比较每一个元素
	for (let i = 0; i < arr1.length; i++) {
		if (arr1[i] !== arr2[i]) {
			return false;
		}
	}
	// 如果两个数组长度相同，且每一个元素都相同，认为这两个数组内容一致
	return true;
}
